text = "The CT scan can be performed as either low-dose CT or as diagnostic CT - ideally with IV contrast if indicated."

# The annotation instances that take place in the text above are listed here
result = [
    ActivityData(span="CT scan"),
    Activity(span="performed"),
    ActivityData(span="low-dose CT"),
    Or(span="or"),
    ActivityData(span="diagnostic CT"),
    Specification(span="IV contrast if indicated"),
]

