text = "* For PET with low-dose CT, an answer can usually be expected the following afternoon"

# The annotation instances that take place in the text above are listed here
result = [
    Observation(span="PET with low-dose CT"),
    ActivityData(span="answer"),
    Activity(span="expected"),
    Specification(span="following afternoon"),
]

