text = (
    "* Check patient's circulation via monitor + Blood pressure + Pulse + Heart rhythm"
)

# The annotation instances that take place in the text above are listed here
result = [
    Activity(span="Check"),
    Input(span="Blood pressure"),
    Input(span="Pulse"),
    Input(span="Heart rhythm"),
]

