text = "Pt. is given an appointment for an outpatient check-up after 1 week. In the outpatient clinic, the bladder is filled with saline the catheter is removed and flow and residual urine are measured."

# The annotation instances that take place in the text above are listed here
result = [
    Guard(span="after 1 week"),
]

